var result = ifScript();

function ifScript() {
    // Get the current date
    var currentDate = new GlideDateTime();

    // Get the delivery_date field value (replace 'delivery_date' with your actual field name)
    var deliveryDate = new GlideDateTime(current.delivery_date);

    // Calculate the difference in days
    var daysDifference = deliveryDate.subtract(currentDate).getDayOfMonth();

    // Check if the difference is greater than 5 days
    if (daysDifference > 5) {
        return 'yes';
    }

    return 'no';
}

// Now 'result' will contain either 'yes' or 'no' based on the condition

